@font-face {
  font-family: "Ilisarniq-Demi";
  src: url("~/public/fonts/Ilisarniq-Demi.eot");
  src: url("~/public/fonts/Ilisarniq-Demi.eot?#iefix")
      format("embedded-opentype"),
    url("~/public/fonts/Ilisarniq-Demi.woff2") format("woff2"),
    url("~/public/fonts/Ilisarniq-Demi.woff") format("font-woff"),
    url("~/public/fonts/Ilisarniq-Demi.ttf") format("truetype");
  font-weight: normal;
  font-style: normal;
}
@font-face {
  font-family: "Ilisarniq-Light";
  src: url("Ilisarniq-Light.eot");
  src: url("Ilisarniq-Light.eot?#iefix") format("embedded-opentype"),
    url("Ilisarniq-Light.woff2") format("woff2"),
    url("Ilisarniq-Light.woff") format("woff"),
    url("Ilisarniq-Light.ttf") format("truetype");
  font-weight: normal;
  font-style: normal;
}
@font-face {
  font-family: "Ilisarniq-DemiItalic";
  src: url("~/public/fonts/Ilisarniq-DemiItalic.eot");
  src: url("~/public/fonts/Ilisarniq-DemiItalic.eot?#iefix")
      format("embedded-opentype"),
    url("~/public/fonts/Ilisarniq-DemiItalic.woff2") format("woff2"),
    url("~/public/fonts/Ilisarniq-DemiItalic.woff") format("font-woff"),
    url("~/public/fonts/Ilisarniq-DemiItalic.ttf") format("truetype");
  font-weight: normal;
  font-style: normal;
}
@font-face {
  font-family: "Ilisarniq-LightItalic";
  src: url("~/public/fonts/Ilisarniq-LightItalic.eot");
  src: url("~/public/fonts/Ilisarniq-LightItalic.eot?#iefix")
      format("embedded-opentype"),
    url("~/public/fonts/Ilisarniq-LightItalic.woff2") format("woff2"),
    url("~/public/fonts/Ilisarniq-LightItalic.woff") format("woff"),
    url("~/public/fonts/Ilisarniq-LightItalic.ttf") format("truetype");
  font-weight: normal;
  font-style: normal;
}

body {
  font-family: "Ilisarniq-Demi";
  color: black;
  margin: 0%;
}

li {
  font-size: 20px;
  padding: 10px;
  margin: 40px;
  margin-top: 0%;
}
span {
  font-family: "Ilisarniq-LightItalic";
  color: green;
}

.menu a:hover {
  cursor: pointer;
}

h1 {
  text-align: center;
  font-size: 60px;
  margin: 70px;
  margin-top: 0px;
}

h2 {
  font-size: 60px;
  margin-bottom: 0px;

  margin-top: 0px;
  letter-spacing: 4px;
  color: rgb(0, 0, 0);
}
h3 {
  font-family: "Ilisarniq-LightItalic";
  font-size: 20px;
  letter-spacing: 3px;
}

h4 {
  font-size: 30px;
}

p {
  font-size: 22px;
  font-family: "Ilisarniq-LightItalic";
}
/* id element----------------------------------------------------------------------------- */

/* wrappers--------------------------------------------------------------------------- */

.menuWrapper {
  display: flex;
  justify-content: flex-end;
  width: 100%;

  /*     background:rgb(100, 225, 13, 0.5) */
}

.body-wrapper {
  display: flex;
  flex-direction: column;
  justify-content: center;
  margin: 2%;
  margin-top: 0%;
}

.nameWrapper {
  /*   margin-right:20%; */
  margin-top: 0%;

  padding: 40px;
  padding-top: 0px;
  padding-bottom: 0px;
  word-spacing: 10px;
  white-space: nowrap;
  position: relative;
  top: -40%;
  /* background:rgba(0 , 0, 0);
   */
}
.logoWrapper {
  width: 100%;
  height: 35px;
  position: relative;
  display: flex;
  justify-content: flex-end;
  margin-right: 2%;
}
.footerWrapper {
  position: relative;
  width: 100%;
  padding: 10px;
  margin-bottom: 0px;

  height: 50px;
  background: rgb(141, 235, 141);

  display: flex;
  justify-content: center;
  align-items: center;
  overflow: hidden;
}

/* Header-------------------------------------------------------------------------------------- */

.topMenu {
  position: relative;
  width: 100%;
  padding: 20px;

  height: 60px;
  background: rgb(245, 245, 245);
  background: rgb(229, 253, 251);
  background: rgb(141, 235, 141);
  display: flex;
  padding-top: 2%;
  padding-bottom: 0%;
  /*   background: red; */
  /*     background: rgb(228, 72, 111); */
  /*   background: linear-gradient( rgb(228, 72, 111), rgb(245, 89, 41)); */
  /*     margin-bottom: 20px; */

  /* align-items: flex-end; */
}

.logoWrapper img {
  max-height: 100%;
  margin-right: 30px;
}

.footerWrapper img {
  height: 30px;
  margin-left: 30px;
  margin-right: 10px;
}

/* Body------------------------------------------------------------------------------------- */

.body-content {
  text-align: justify;
  width: 50%;
  padding: 20px;
  align-self: center;
  overflow: hidden;

  color: black;
}

.videoImg {
  width: 768px;
  height: 432px;
}

#paGang {
  object-position: 0% 20%;
  opacity: 0.8;
}

.cvLista li {
  list-style: none;
  font-size: 22px;
  font-family: "Ilisarniq-LightItalic";
  margin: 0px;
}

.menu {
  display: flex;
  white-space: nowrap;

  position: absolute;

  justify-content: space-between;
  right: 0px;
  /*  margin-top: 2%; */
}
@media only screen and (max-width: 600px) {
  .headerWrapper {
    display: flex;
    flex-direction: column;
  }
  .nameWrapper {
    position: static;
    padding: 0;
    word-spacing: 0%;
  }
  .logoWrapper {
    display: inline-block;
    margin-top: 10%;
    img {
      padding: 40px;
    }
  }
  .logoWrapper img {
    padding: 50px;
  }

  .topMenu {
    padding: 20px;
    display: inline;
    /*  position: absolute */
  }
  h2 {
    font-size: 38px;
  }
}
.routerViewWrapper {
  margin-top: 200px;
}
